import React, { useState, useEffect, Fragment } from "react";
import { connect } from 'react-redux'
import { update_user_profile } from "../actions/profile";
import { delete_account } from "../actions/auth";
import FriendCard from "./friendCard";
const Friends = ({

    first_name_global,
    last_name_global,
    phone_global,
    city_global,
    friendlist_global,
    groups_global

}) => {



    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        phone: '',
        city: '',
    })



    const { first_name, last_name, phone, city } = formData

    //For initial global values
    useEffect(() => {
        setFormData({
            first_name: first_name_global,
            last_name: last_name_global,
            phone: phone_global,
            city: city_global,
        });
    }, [first_name_global, last_name_global, phone_global, city_global])

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value })

    const onSubmit = e => {
        e.preventDefault();
        update_user_profile(first_name, last_name, phone, city);//it async function
    }

    return (
        <Fragment>
            <div className="card-group">
                {friendlist_global.length === 0 ? <>
                    <div class="card">
                        <div class="card-header">
                            ChatApp
                        </div>
                        <div class="card-body">
                            <blockquote class="blockquote mb-0">
                                <h1>No Friends !</h1>
                                <footer class="blockquote-footer"> <cite title="Source Title">ADD FRIENDS</cite></footer>
                            </blockquote>
                        </div>
                    </div>
                </> :
                    friendlist_global.map(
                        (myfriend) => {
                            return (
                                <FriendCard title={myfriend} />
                            )
                        }
                    )
                }
            </div>
        </Fragment>
    );

};

const mapStateToProps = state => ({

    first_name_global: state.profile.first_name,
    last_name_global: state.profile.last_name,
    phone_global: state.profile.phone,
    city_global: state.profile.city,
    friendlist_global: state.friends,
    groups_global: state.groups,
    //these are the variables passed as props globally tomaintain state of application
})

export default connect(mapStateToProps, { delete_account, update_user_profile })(Friends);
