import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
    LOGIN_FAIL,
    LOGIN_SUCCESS,
    LOGOUT_FAIL,
    LOGOUT_SUCCESS,
    AUTHENTICATED_FAIL,
    AUTHENTICATED_SUCCESS
} from '../actions/types'

const initialState = {
    isAuthenticated: null,
    username: '',
    fist_name: '',
    last_name: '',
    phone: '',
    city: '',
}

// eslint-disable-next-line import/no-anonymous-default-export
export default function (state = initialState, action) {

    const { type,payload } = action;
    switch (type) {
        case AUTHENTICATED_SUCCESS:
        case AUTHENTICATED_FAIL:
            return {
                 ...state,
                 isAuthenticated:payload
             }
             
        case REGISTER_SUCCESS:
            return {
                ...state,
                isAuthenticated: false
            }
    
        case LOGIN_SUCCESS:
            return {
                ...state,
                isAuthenticated:true,
                username: payload
            }    
        
        case LOGOUT_SUCCESS:
            return {
                ...state,
                isAuthenticated:false,
                username:''
            }     
        
        case REGISTER_FAIL:
        case LOGIN_FAIL:  
        case LOGOUT_FAIL:  
                return state; 
            
                
                
                
        default: return state    

}

}
