import React, { useState, useEffect, Fragment } from "react";
import { connect } from 'react-redux'
import { update_user_profile } from "../actions/profile";
import { delete_account } from "../actions/auth";
import FriendCard from "./FriendCard";
const Friends = ({

    first_name_global,
    last_name_global,
    phone_global,
    city_global,
    friendlist_global,
    groups_global

}) => {

    console.log(friendlist_global)

    return (
        <Fragment>
            <div className="card-group">
                {friendlist_global.length === 0 ? <>
                    <div class="card">
                        <div class="card-header">
                            ChatApp
                        </div>
                        <div class="card-body">
                            <blockquote class="blockquote mb-0">
                                <h1>No Friends !</h1>
                                <footer class="blockquote-footer"> <cite title="Source Title">ADD FRIENDS</cite></footer>
                            </blockquote>
                        </div>
                    </div>
                </> :
                    friendlist_global.map(
                        (myfriend) => {
                            return (
                                <FriendCard title={myfriend} />
                            )
                        }
                    )
                }
            </div>
        </Fragment>
    );

};

const mapStateToProps = state => ({

    first_name_global: state.profile.first_name,
    last_name_global: state.profile.last_name,
    phone_global: state.profile.phone,
    city_global: state.profile.city,
    friendlist_global: state.friends,
    groups_global: state.groups,
    //these are the variables passed as props globally tomaintain state of application
})

export default connect(mapStateToProps, { })(Friends);
